import React from 'react';
import ReactDOM from 'react-dom';

const Part = (props) => {
	return (
		<p>
			{props.name} {props.exercises}
		</p>
	);
};

const Header = (props) => {
	return <h1>{props.course}</h1>;
};

const Content = (props) => {
	return (
		<div>
			<Part parts={props[0]} />
			<Part parts={props[1]} />
			<Part parts={props[2]} />
		</div>
	);
};

const Total = (props) => {
	// console.log(props);
	return <p>Number of exercises {props[0][exercises] + props[1][exercises] + props[2][exercises]}</p>;
};

const App = () => {
	// const-definitions
	const course = 'Half Stack application development';
	const parts = [
		{
			name: 'Fundamentals of React',
			exercises: 10
		},
		{
			name: 'Using props to pass data',
			exercises: 7
		},
		{
			name: 'State of a component',
			exercises: 14
		}
	];

	return (
		<div>
			<Header course={course} />
			<Content parts={parts} />
			<Total parts={parts} />
		</div>
	);
};

ReactDOM.render(<App />, document.getElementById('root'));
