import React from 'react';
import ReactDOM from 'react-dom';

const Part = (props) => {
	return (
		<p>
			{props.name} {props.exercises}
		</p>
	);
};

const Header = (props) => {
	return <h1>{props.course}</h1>;
};

const Content = (props) => {
	return (
		<div>
			<Part parts={props[0]} />
			<Part parts={props[1]} />
			<Part parts={props[2]} />
		</div>
	);
};

